                    frm.clear_table("ctns");

                    // Add new CTNs to child table
                    r.message.needed_ctns.forEach(row => {
                        const item = frm.add_child("ctns");
                        item.ctn = row;
                    });
                    frm.refresh_field("ctns");

                    // Save CTN details in memory (not saved to DB)
                    frm.doc._ctn_details_map = r.message.ctn_details;

                    // Render CTN details as HTML
                    const html = generateCTNHtml(r.message.needed_ctns, r.message.ctn_details);
                    frm.fields_dict["ctn_details"].$wrapper.html(html);
                }
            }
        });
	},

	generate_transfer_btn(frm) {
	    const ctns = (frm.doc.ctns || []).map(row => row.ctn);
	    const ctn_details = frm.doc._ctn_details_map || {};

	    if (ctns.length === 0) {
	        frappe.msgprint(__('No CTNs selected'));
	        return;
	    }

	    const { custom_ctn_boxs, items } = buildCTNTransferData(ctns, ctn_details);

	    if (items.length === 0) {
	        frappe.msgprint(__('No items found in selected CTNs'));
	        return;
	    }

	    frappe.call({
	        method: "frappe.client.insert",
	        args: {
	            doc: {
	                doctype: "Stock Entry",
	                stock_entry_type: "Material Transfer",
	                from_warehouse: frm.doc.from,
	                to_warehouse: frm.doc.to,
	                custom_transfer_by_ctns: 1,
	                custom_ctn_boxs,
	                items
	            }
	        },
	        callback: function(res) {
	            const docname = res.message.name;
	            frappe.set_route("Form", "Stock Entry", docname);
	        }
	    });
	}
});

// ðŸ“¦ Helper to build CTN -> HTML table for display
function generateCTNHtml(ctns, ctn_details) {
    console.log("ctns --> " , ctns , "ctn_details --> " , ctn_details)
    let html = "";

    ctns.forEach(ctn => {
        html += `<h4 style="margin-top:16px; color:#4a4a4a;">ðŸ“¦ CTN: ${ctn}</h4>`;
        html += `<table class="table table-bordered" style="width:100%; margin-bottom: 20px;">
                    <thead>
                        <tr>
                            <th style="width: 60%;">Item Code</th>
                            <th style="width: 40%;">Quantity</th>
                        </tr>
                    </thead>
                    <tbody>`;

        (ctn_details[ctn] || []).forEach(item => {
            html += `
                <tr>
                    <td>${item.item}</td>
                    <td>${item.qty}</td>
                </tr>`;
        });

        html += `</tbody></table>`;
    });

    return html || "<p>No CTNs found.</p>";
}

// ðŸ§  Helper to convert CTN details to CTN box and item list
function buildCTNTransferData(ctns, ctn_details) {
    const custom_ctn_boxs = [];
    const itemMap = {};

    console.log(ctns , ctn_details )

    ctns.forEach(ctn => {
        custom_ctn_boxs.push({ ctn: ctn });

        (ctn_details[ctn] || []).forEach(({ item, qty }) => {
            if (!itemMap[item]) {
                itemMap[item] = 0;
            }
            itemMap[item] += qty;
        });
    });

    const items = Object.keys(itemMap).map(item_code => ({
        item_code,
        qty: itemMap[item_code]
    }));

    return { custom_ctn_boxs, items };
}

