function update_items_from_ctns(frm) {
    
    const is_receipt = frm.doc.stock_entry_type === "Material Receipt";
    
	if (!frm.doc.custom_transfer_by_ctns || !frm.doc.custom_ctn_boxs.length) return;

	frm.clear_table("items");
	frm.refresh_field("items");

    const ctn_names = frm.doc.custom_ctn_boxs.map(row => row.ctn)

    console.log("the ctns ==> " , ctn_names)
    
    frappe.call({
        method : "pos_ar.api.get_items_from_ctns",
        args : { ctn_list : ctn_names},
        freeze : true,
        freeze_message : "Fetching items from CTNs ...",
        callback : function(r){
            if(!r.message) return;
            frm.clear_table("items");
            r.message.forEach(item => {
                frm.add_child("items" , {
                    item_code         : item.item_code,
                    qty               : item.qty ,
                    uom               : item.uom || "",
                    stock_uom         : item.uom || "",
                    transfer_qty      : item.qty * item.conversion_factor,
                    conversion_factor : item.conversion_factor || 1 ,
                    s_warehouse       : is_receipt ? "" :item.s_warehouse,
                    t_warehouse       : frm.doc.to_warehouse //default to warhouse on issue just dont set it.
                })
            })
            
            frm.refresh_field("items");
            frappe.show_alert("Items updated from CTNs")
        }
    })

}

