/**
 * A record is "fulfilled" iff:
 *  - command_achat is set
 *  - facture_achat is set
 *  - for every child row: alter == 0
 * Hides/shows the "generate_alter"/links accordingly.
 */
function evaluate_fulfillment_and_toggle_buttons(frm) {
    let hasPO = false;
    if (frm.doc.command_achat != "---") hasPO = true;

    let hasPI = false;
    if (frm.doc.facture_achat != "---") hasPI = true;

    const rows = frm.doc.items || [];
    const itemsOk = rows.length > 0
        ? rows.every(r => (r.alter ?? 0) == 0)
        : false;

    const canShowAlt = hasPO && hasPI && !itemsOk;
    console.log("po:", hasPO, "PI:", hasPI, "itemsOK:", itemsOk, "show:", canShowAlt);

    frm.toggle_display("generate_alter", canShowAlt);
    frm.toggle_display("alter_command_achat", canShowAlt);
    frm.toggle_display("alter_facture_achat", canShowAlt);

    frm.dashboard.clear_headline();
    if (hasPO && hasPI && itemsOk) {
        frm.dashboard.set_headline(__("âœ… Commande & Facture complÃ¨tes â€” tous les articles sont couverts."));
    } else {
        const missing = [];
        if (!hasPO) missing.push(__("commande dâ€™achat manquante"));
        if (!hasPI) missing.push(__("facture dâ€™achat manquante"));
        if (!itemsOk) missing.push(__("There are missed items"));
        frm.dashboard.set_headline(__("â›” Incomplet: {0}", [missing.join(" â€¢ ")]));
    }
}


function export_child_table(frm) {
    let rows = frm.doc.items || [];
    if (!rows.length) {
        frappe.msgprint("No rows to export.");
        return;
    }

    // Pick fields to export
    const headers = ["Item", "Sold", "Found", "Alter", "Not Found"];
    const keys    = ["item", "sold", "found", "alter", "not_found"];

    // Build CSV
    let csv = headers.join(",") + "\n";
    rows.forEach(r => {
        csv += keys.map(k => `"${r[k] ?? ""}"`).join(",") + "\n";
    });

    // Download CSV
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `${frm.doc.name || "table_export"}.csv`;
    link.click();
}


function install_overstock_css() {
  frappe.dom.set_style(`
    /* whole row turns light red when it has class "overstock" */
    .grid-row.overstock {
      background: #faa7a7 !important;
    }
  `, "buy-the-sold-overstock-css");
}

function colorize_items_grid(frm) {
  const grid = frm.fields_dict["items"]?.grid;
  if (!grid) return;

  (grid.grid_rows || []).forEach(gr => {
    const d = gr.doc || {};
    const onStock  = parseFloat(d.on_stock ?? 0);
    const bestSell = parseFloat(d.best_sell ?? 0);
    $(gr.wrapper).toggleClass("overstock", onStock > bestSell);
  });
}

/**
 * Attach strong hooks to keep row coloring after pagination/sort/refresh.
 * Use once per form instance.
 */
function attach_items_grid_hooks(frm) {
  const grid = frm.fields_dict?.items?.grid;
  if (!grid || grid.__colorHooksAttached) return;

  install_overstock_css();

  // 1) Hook grid.refresh â†’ repaint after render
  if (typeof grid.refresh === "function" && !grid.__orig_refresh) {
    grid.__orig_refresh = grid.refresh.bind(grid);
    grid.refresh = function () {
      const ret = grid.__orig_refresh(...arguments);
      requestAnimationFrame(() => colorize_items_grid(frm));
      return ret;
    };
  }

  // 2) Hook grid.refresh_row â†’ repaint after partial updates
  if (typeof grid.refresh_row === "function" && !grid.__orig_refresh_row) {
    grid.__orig_refresh_row = grid.refresh_row.bind(grid);
    grid.refresh_row = function () {
      const ret = grid.__orig_refresh_row(...arguments);
      requestAnimationFrame(() => colorize_items_grid(frm));
      return ret;
    };
  }

  // 3) Listen to pagination buttons (varies by Frappe versions/themes)
  const $w = $(grid.wrapper);
  $w.on("click", ".grid-pagination .btn, .grid-footer .btn, .btn-paging", () => {
    requestAnimationFrame(() => colorize_items_grid(frm));
  });

  // 4) MutationObserver backup (covers DOM swaps/reclasses)
  const bodyEl =
    $w.find(".grid-body .rows")[0] || $w.find(".grid-body")[0] || $w[0];

  if (bodyEl) {
    const observer = new MutationObserver(() => {
      colorize_items_grid(frm);
    });
    observer.observe(bodyEl, {
      childList: true,
      subtree: true,
      attributes: true,
      attributeFilter: ["class", "style"],
    });
    grid.__colorObserver = observer;
  }

  grid.__colorHooksAttached = true;

  // First paint (if rows visible)
  requestAnimationFrame(() => colorize_items_grid(frm));
}

